sources...\\styles\\pages\\_home.scss:
.home {
  width: 100vw;
  height: 100vh;
  margin-bottom: 4rem;
  position: relative;
  background-image: url("./../assets/landing_img.png");
  background-repeat: no-repeat;
  background-size: cover;
  background-position: bottom;

  &__rectangle {
    position: absolute;
    width: 100%;
    height: 100%;
    left: 50%;
    bottom: 0;
    transform: translateX(-50%);
    display: flex;
    flex-direction: column;
    align-items: start;

    // background: #ffc936;
    // border-radius: 8px 8px 0 0;

    @include mobile {
      width: 85%;
    }
    &__icon {
      // background-image: url("./../assets/logo_bg.png");
      background-repeat: no-repeat;
      background-size: cover;
      background-position: bottom;
      position: absolute;
      right: 0;
      top: 10%;
      transform: translate(10%, 0%);
      height: 500px;
      width: 500px;
      border-radius: 4px;
      display: grid;
      place-items: center;
      @include mobile {
        height: 26vh;
        width: 26vh;
        z-index: -1;
        opacity: 0.5;
        right: -10%;
        top: 5%;
      }
      img {
        height: 90%;
        width: 90%;
        margin-left: -10px;
        opacity: 0.5;
      }
    }
    &__content {
      width: 40%;
      color: #ffffff;
      margin-top: 14.5%;
      font-size: 18px;
      font-weight: 300;
      line-height: 150%;
      text-align: justify;
      display: flex;
      flex-direction: column;
      margin-left: 5rem;
      z-index: 1;

      @include mobile {
        margin: auto;
        margin-bottom: 0rem;
        width: 90%;
        font-size: 1.8vh;
      }
      & > p {
        margin: auto auto 2rem auto;
      }
      &__dates {
        padding-top: 0.6rem;
        font-weight: 500;
        font-size: 16px;
        display: flex;
        align-items: center;
        vertical-align: middle;
        @include mobile {
          padding-top: 2rem;
          font-size: 1.6vh;
        }
        span {
          font-size: 36px;
          font-weight: 500;
          margin: 0 16px;
          color: $secondary-color;
          @include mobile {
            font-size: 3.4vh;
          }
        }
        .date__from {
          margin-top: -24px;
        }
        .date__to {
          margin-bottom: -18px;
        }
      }
    }
    &__buttons {
      margin: 2rem 0rem 2rem 5rem;
      width: 40%;
      display: flex;
      justify-content: center;
      gap: 8vw;
      align-items: center;

      @include mobile {
        margin: 2rem auto 2rem auto;
        width: 90%;
        justify-content: space-between;
        flex-direction: column;
        gap: 1rem;
        align-items: center;
      }

      & > a {
        display: flex;
        gap: 8px;
        padding: 10px 20px;
        align-items: center;
        border-radius: 4px;
        text-decoration: none;
        width: 100%;
        @include mobile {
          width: 80%;
        }
        img {
          height: 36px;
          width: 36px;
          @include mobile {
            height: 24px;
            width: 24px;
          }
        }
        p {
          font-size: 13px;
          font-weight: 600;
          @include mobile {
            font-weight: 500;
            font-size: 12px;
          }
        }
      }
      &__button-1 {
        background: #3770ff;
        border: 2px solid#3770FF;
        color: #ffffff;
      }
      &__button-2 {
        border: 2px solid #ffffff;
        color: #ffffff;
      }
    }
  }
}

.about {
  &__breaker {
    margin-bottom: 6rem;
    height: 200px;
    width: 100%;
    background-image: url("./../assets/brochureContainer.png");
    background-position: right;
    transform: rotate(180deg);
    background-size: contain;
    background-repeat: no-repeat;
    @include tablet {
      display: none;
    }
  }
}

