    &__content {
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: end;
      width: 80%;
      height: 100%;
      position: absolute;
      top: 0;
      right: 15%;

      @include mobile {
        align-items: flex-end;
        right: 5%;
      }

      color: $accent-color;

      p {
        font-weight: 500;
        margin: 10px 0;
        font-size: 20px;
        text-align:right;

        @include mobile {
          font-size: 18px;
        }
      }
      :nth-child(2) {
        font-size: 14px;
        font-weight: 300;
        text-align:right;
      }
      .button {
        text-decoration: none;
        color: #ffffff;
        margin-top: 1.6rem;
        border: 2px solid $secondary-color;
        padding: 1rem 2rem;
        cursor: pointer;
        font-size: 18px;
        position: relative;
        z-index: 2;

