  0% {
    transform: rotate(0deg);
    stroke-opacity: 1;
  }
  80% {
    transform: rotate(360deg);
    stroke-opacity: 0.4;
  }
  100% {
    transform: rotate(0deg);
    stroke-opacity: 1;
  }
}

